import './App.css';
import { useState } from 'react';



function App() {

  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState(0);

  const mostrarDatos = () => {
    alert(nombre)
  }

  return (
    <div className="App">
      <div className="datos">
        <label><a>Nombre: </a><input 
        onChange={(e) => {
          setNombre(e.target.value);
        }}
        type="text"/></label>
        <label>Edad: <input 
        onChange={
          (e) => {
            setEdad(e.target.value);
          }}
        type="text"/></label>
        <label>Pais: <input 
        onChange={
          (e) => {
            setPais(e.target.value);
          }}
        type="text"/></label>
        <label>Cargo: <input 
        onChange={
          (e) => {
            setCargo(e.target.value);
          }}
        type="text"/></label>
        <label>Anios: <input 
        onChange={
          (e) => {
            setAnios(e.target.value);
          }}
        type="number"/></label>
        <button onClick={mostrarDatos}>Registrar</button>
      </div>
    </div>
  );
}

export default App;
