import './App.css';
import { useState } from 'react';
import Axios from 'axios';
import 'bootstrap/dist/css/bootstrap.min.css';




function App() {

  const [nombre, setNombre] = useState("");
  const [edad, setEdad] = useState(0);
  const [pais, setPais] = useState("");
  const [cargo, setCargo] = useState("");
  const [anios, setAnios] = useState(0);

  const [empleadosList, setEmpleadosList] = useState([]);

  const add = () => {
    Axios.post("http://localhost:3001/create",{
      nombre: nombre,
      edad: edad,
      pais: pais,
      cargo: cargo,
      anios: anios
    }).then(()=>{
      getEmpleados();
      alert("Empleado Registrado Con Exito!!!");
    });
  }

  const getEmpleados = () => {
    Axios.get("http://localhost:3001/empleados").then((response)=>{
      setEmpleadosList(response.data);
    });
  }

 getEmpleados();

  return (
  <div className="container">
    <div className="App">
      <div className="lista">
      {
        empleadosList.map((val,key)=>{
          return <div className="">
            {val.nombre}
          </div>
        })
      }
      </div>
    </div>
    <div className="card text-center">
      <div className="card-header">
        Gestion De Empleados
      </div>
      <div className="card-body">
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Nombre:</span>
          <input type="text" 
            onChange={(e) => {
              setNombre(e.target.value);
            }}
            className="form-control" placeholder="Ingrese Edad" aria-label="Username" aria-describedby="basic-addon1">
          </input>
        </div>
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Edad:</span>
          <input type="number" 
            onChange={(e) => {
              setEdad(e.target.value);
            }}
            className="form-control" placeholder="Ingrese Edad" aria-label="Username" aria-describedby="basic-addon1">
          </input>
        </div>
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Pais:</span>
          <input type="text" 
            onChange={(e) => {
              setPais(e.target.value);
            }}
            className="form-control" placeholder="Ingrese Pais" aria-label="Username" aria-describedby="basic-addon1">
          </input>
        </div>
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Cargo:</span>
          <input type="text" 
            onChange={(e) => {
              setCargo(e.target.value);
            }}
            className="form-control" placeholder="Ingrese Cargo" aria-label="Username" aria-describedby="basic-addon1">
          </input>
        </div>
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1">Anios:</span>
          <input type="number" 
            onChange={(e) => {
              setAnios(e.target.value);
            }}
            className="form-control" placeholder="Ingrese Anios" aria-label="Username" aria-describedby="basic-addon1">
          </input>
        </div>
      </div>
      <div className="card-footer text-muted">
        <button className="btn btn-success" onClick={add}>Registrar</button>
      </div>
    </div>
  </div>
);
}

export default App;
